SELECT MIN(mi.movie_id) FROM keyword AS k INNER JOIN movie_keyword as mk ON mk.keyword_id = k.id INNER JOIN movie_info AS mi ON mi.movie_id = mk.movie_id WHERE k.keyword LIKE '%sequel%' AND mi.info LIKE '%USA%';
