SELECT MIN(mc.note), MIN(t.title), MIN(t.production_year) FROM movie_companies AS mc INNER JOIN movie_info_idx AS mi_idx ON mc.movie_id = mi_idx.movie_id INNER JOIN title AS t ON t.id = mi_idx.movie_id WHERE mc.note NOT LIKE '%(as Metro-Goldwyn-Mayer Pictures)%';
SELECT MIN(mc.note), MIN(t.title), MIN(t.production_year) FROM title AS t INNER JOIN movie_info_idx AS mi_idx ON t.id = mi_idx.movie_id INNER JOIN movie_companies AS mc ON mc.movie_id = mi_idx.movie_id WHERE mc.note NOT LIKE '%(as Metro-Goldwyn-Mayer Pictures)%';
