SELECT MIN(mi_idx.info) AS rating, MIN(t.title) AS movie_title FROM movie_keyword AS mk INNER JOIN title AS t ON t.id = mk.movie_id INNER JOIN movie_info_idx AS mi_idx ON t.id = mi_idx.movie_id INNER JOIN keyword AS k ON k.id = mk.keyword_id INNER JOIN info_type AS it ON it.id = mi_idx.info_type_id WHERE it.info = 'rating' AND k.keyword LIKE '%sequel%' AND mi_idx.info > '2.0' AND t.production_year > 2010;
